import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { User } from '../user.model';
import { DataServices } from '../data.services';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  //   styleUrls: ['./create-users.component.css']
})
export class FormComponent implements OnInit {
  idSelected:number
  // @Output() usuarioCreado: EventEmitter<User> = new EventEmitter<User>()
  constructor(private dataServices:DataServices){
    
  }
    
  ngOnInit(): void {
    this.dataServices.userSelected$.subscribe(({ user, index }) => {
      this.nombreI = user.nombre
      this.apellidoI = user.apellido
      this.idSelected = index
    });
  }

  nombreI: string = '';
  apellidoI: string = '';

  agregarUsuario() {
    let usuario = new User(this.nombreI, this.apellidoI);
    if(this.idSelected){
      this.dataServices.updateUser(this.idSelected, usuario)
    } else {
      this.dataServices.newUser(usuario)
    }
    
    this.nombreI = '';
    this.apellidoI = '';
    // this.dataServices.setUsers(this.dataServices.getUsers())
  }
}
<form class="form-row">
  <div class="form-group col-md-2">
    <label for="nombre">
      <input
        type="text"
        name="nombre"
        id="nombre"
        placeholder="Nombre"
        class="form-control"
        [(ngModel)]="nombreI"
      />
    </label>
  </div>
  <div class="form-group col-md-2">
    <label for="apellido">
      <input
        type="text"
        name="apellido"
        id="apellido"
        placeholder="Apellido"
        class="form-control"
        [(ngModel)]="apellidoI"
      />
    </label>
  </div>
  <div class="col">
    <button type="submit" class="btn btn-primary" 
    (click)="agregarUsuario()">
      Agregar usuario
    </button>
  </div>
  <br />
</form>
