import { Component, OnInit} from '@angular/core';
import {User} from './user.model'
import { DataServices } from './data.services';

@Component({
  selector: 'app-create-users',
  templateUrl: './create-users.component.html',
  styleUrls: ['./create-users.component.css'],
  providers: [DataServices]
})
export class CreateUsersComponent implements OnInit {
  users:User[] = []

  constructor(private dataServices:DataServices ){
    dataServices.updateUser.sub
  }

  ngOnInit(): void {

    this.dataServices.getUsers()
      .subscribe({
        next: (res:User[]) => {
          this.users = res
          this.dataServices.setUsers(res)
        }
      })

  }

  ingresarUsuario(user: User){
    this.dataServices.newUser(user)
  }

}
<div>
  <div class="row">
    <h1>Agregando usuarios</h1>
    <hr>
    <app-form>

    </app-form>
    <hr>
    <app-user *ngFor="let user of users; let i = index" [user] = "user" [i] = "i">

    </app-user>
    <br>
    <hr>
    <br>
    <br>
  </div>
</div>
