import { Component, OnInit } from '@angular/core';
import { Movimiento } from './movimiento.model';
import { deleteMov } from './delete.model';

@Component({
  selector: 'app-presupuesto',
  templateUrl: './presupuesto.component.html',
  // styleUrls: ['./presupuesto.component.css']
})
export class PresupuestoComponent implements OnInit{
    egresos:Movimiento[] = [ new Movimiento("Telefono celular", 450), new Movimiento("Pago de renta hogar", 250)]
    ingresos:Movimiento[] = [ new Movimiento("Sueldo/Salario", 1500), new Movimiento("Venta de articulos varios", 850)]
    tipo:string = "ingreso" 

    ngOnInit(){
      // this.calculoEgresos()
      // this.calculoIngresos()
      // this.calculoPorcentual()
    }

    calculoPorcentual(){
      const e = this.calculoEgresos()
      const i = this.calculoIngresos()
      let result
      if(i > 0){
        result = (e / i * 100)
      } else {
        result = 0
      }
      return result.toFixed(2)
    }

    calculoEgresos(){
      let resultado = 0
      this.egresos.forEach(item => {
        resultado += item.monto
      })
      return resultado;
    }

    calculoIngresos(){
      let resultado = 0
      this.ingresos.forEach(item => {
        resultado += item.monto
      })
      return resultado;
    }

    deleteMovimiento(data:deleteMov){
      if(data.tipo == "ingresos"){
        this.ingresos.splice(data.index, 1)
      } else {
        this.egresos.splice(data.index, 1)
      }
    }

    setTipo(tipo:any){
      this.tipo = tipo.target.value
    }

    newMovimiento(descripcion:HTMLInputElement, monto:HTMLInputElement){
      let movimiento = new Movimiento(descripcion.value, parseFloat(monto.value))
      if(this.tipo == "ingreso"){
        this.ingresos.push(movimiento)

      } else {
        this.egresos.push(movimiento)
        // this.calculoEgresos()
      }
      descripcion.value = ""
      monto.value = ""
      // this.calculoPorcentual()
    }
}
<div class="p_header">
    <h2>Presupuesto Disponible</h2>
    <p>${{calculoIngresos() - calculoEgresos()}}</p>
    <div class="p_detail ingreso">
        <p>INGRESOS</p>
        <P>{{calculoIngresos() | currency: "USD" : "symbol": "1.2-2" }}</P>
    </div>
    <div class="p_detail egreso">
        <P>EGRESOS</P>
        <P>{{calculoEgresos() | currency: "USD" : "symbol": "1.2-2"}}</P>
        <P>{{calculoPorcentual()}}%</P>
    </div>
</div>
<div class="p_input">
    <div>
    <select name="" id="" (change)="setTipo($event)">
        <option value="ingreso">+</option>
        <option value="egreso">-</option>
    </select>
    <input type="text" #descripcion>
    <input type="number" #monto>
    <!-- <ion-icon name="checkmark-circle-outline"></ion-icon> -->
    <button (click) = "newMovimiento(descripcion, monto)">Save</button>
</div>
</div>
<div class="p_child">
    <app-ingresos 
    [ingresos] = "ingresos"
    (delete) = "deleteMovimiento($event)"
    ></app-ingresos>
    <app-egresos [egresos] = "egresos" (delete) = "deleteMovimiento($event)" [totalIngreso] = "calculoIngresos()"></app-egresos>
</div>
