import { User } from "./user.model";
import { EventEmitter, Injectable } from "@angular/core"
import { HttpClient } from "@angular/common/http"


@Injectable()
export class DataServices{
    url:string = "https://totemic-antenna-298717-default-rtdb.firebaseio.com"
    users: User[] = [new User("Marco", "Cardenas"), new User("Lucas", "Cardenas")]

    constructor(private httpClient:HttpClient){}

    createUsers(usuarios:User[]){
        this.httpClient.post(this.url, usuarios)
            .subscribe({
                next: (res) => console.log('respuesta:' + res),
                error: (error) => console.log('error: ' + error);
            })
    }

    // saludar = new EventEmitter<string>();

    newUser(user: User){
        this.users.push(user)
        this.createUsers(this.users)
    }

    // saludar(nombre:string){
