import { Component } from '@angular/core';

@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
  styleUrls: ['./calculadora.component.css']
})
export class CalculadoraComponent {
  a:any = ""
  b:any = ""
  resultado:number = 0
  sumar(){
    (this.a + this.b);

  }
}
<div class="container">
    <div class="row">
        <h1>Aplicación de calculadora</h1>
        <p>Operando A:</p>
        <input type="number" placeholder="Ingresa el operando A" class="form-control" [(ngModel)]="a">
        <p>Operando B:</p>
        <input type="number" placeholder="Ingresa el operando B" class="form-control" [(ngModel)]="b">
        <div class="col">
            <br>
            <button class="btn btn-primary" (click)="sumar()">Calcular</button>
            <p>El resultado es {{resultado}} </p>
        </div>
    </div>
</div>
